import { Component, OnInit } from '@angular/core';
import { Cliente } from '../cliente';
import { ClienteService } from '../cliente.service';
import { ActivatedRoute } from '@angular/router';
import Swal from 'sweetalert2';
import { HttpEventType } from '@angular/common/http';

@Component({
  selector: 'detalle-cliente',
  templateUrl: './detalle.component.html',
  styleUrls: ['./detalle.component.css']
})
export class DetalleComponent implements OnInit{
  progreso: number = 0;
  cliente: Cliente | null = null;
  titulo: string = "Detalle del cliente";
  public fotoSeleccionada: File | null = null;
  constructor( private clienteService: ClienteService, private activatedRoute: ActivatedRoute){

  }
  ngOnInit(): void {
    this.activatedRoute.paramMap.subscribe(params =>{
      let id: number = Number(params.get('id'));
      if(id){
this.clienteService.getCliente(id).subscribe(cliente =>{
  this.cliente=cliente;
})
      }
    }
    );
  }

  seleccionarFoto(event : any){
this.fotoSeleccionada=event.target.files[0];
console.log(this.fotoSeleccionada)
if (this.fotoSeleccionada && this.fotoSeleccionada.type.indexOf('image') < 0) {
  Swal.fire('Error', 'El formato debe ser tipo imagen', 'error');
}

  }
//---------------------code del tutorial----------------------
  /*subirFoto() {
    if (!this.cliente || !this.fotoSeleccionada) {
      Swal.fire('Error', 'Selecciona una foto', 'error');
      return;
    }

    this.clienteService.subirFoto(this.fotoSeleccionada, this.cliente.id).subscribe(
      (cliente: any) => {
        this.cliente = cliente;
        Swal.fire('Éxito', 'La foto se ha subido con éxito', 'success');
      },
      (error: any) => {
        console.error(error);
        Swal.fire('Error', 'Error al subir la foto', 'error');
      }
    );
  }*/
  //barra de progreso por mejorara
  subirFoto() {
    if (!this.cliente || !this.fotoSeleccionada) {
      Swal.fire('Error', 'Selecciona una foto', 'error');
      return;
    }

    const formData = new FormData();
    formData.append('archivo', this.fotoSeleccionada);
    formData.append('id', this.cliente.id.toString());

    this.clienteService.subirFoto(this.fotoSeleccionada, this.cliente.id).subscribe(
      (event: any) => {
        if (event.type === HttpEventType.UploadProgress) {
          this.progreso = Math.round((event.loaded / event.total) * 100);
        } else if (event.type === HttpEventType.Response) {
          this.cliente = event.body.cliente as Cliente;
          Swal.fire('Éxito', 'La foto se ha subido con éxito', 'success');
        }
      },
      (error: any) => {
        console.error(error);
        Swal.fire('Error', 'Error al subir la foto', 'error');
      }
    );
  }

}

<div class="card bg-dark text-white my-5">
    <div class="card-header">{{titulo}}</div>
    <div class="card-body">
<div class="container">
    <div class="row">
        <div class="col-sm">
            <ul *ngIf="cliente" class="list-group text-dark mb-3">
                <li class="list-group-item active">{{cliente?.nombre}} </li>
                <li class="list-group-item">{{cliente?.apellido}} </li>
                <li class="list-group-item">{{cliente?.email}} </li>
                <li class="list-group-item">{{cliente?.createAt | date:"fullDate"}} </li>


            </ul>
            <div class="input-group">
                <input (change)="seleccionarFoto($event)" type="file" class="form-control" id="inputGroupFile04" aria-describedby="inputGroupFileAddon04" aria-label="Upload">
                <button (click)="subirFoto()" [disabled]="!fotoSeleccionada" class="btn btn-outline-secondary" type="button" id="inputGroupFileAddon04">Subir Foto</button>
              </div>
        </div>
        <div class="col-sm">
<img *ngIf="cliente?.foto" src="http://localhost:8080/api/uploads/img/{{cliente?.foto}}" alt="{{cliente?.foto}}" class="img-thumbnail rounded">
        </div>
       <!--
        -------------------barra de progreso----------------------> 
        <div *ngIf="progreso > 0">
            <div class="progress">
              <div class="progress-bar" [style.width.%]="progreso">
                {{ progreso }}%
              </div>
            </div>
          </div>
    </div>
</div>



