import { Component, OnInit } from '@angular/core';
import { Cliente } from './cliente';
import { ClienteService } from './cliente.service';
import { ActivatedRoute, Router } from '@angular/router';
import Swal from 'sweetalert2';
import { tap } from 'rxjs';


@Component({
  selector: 'app-form',
  templateUrl: './form.component.html'
})
export class FormComponent implements OnInit{

public cliente: Cliente=new Cliente;
public titulo: string="Crear cliente";
public errores: any [] | undefined;
isSubmitting = false;

constructor( private clienteService: ClienteService, 
  private router: Router, private activatedRoute: ActivatedRoute ){

}
ngOnInit(){
  this.cargarCliente();

}


cargarCliente():void{
this.activatedRoute.params.subscribe(params =>{
  let id= +params['id']
  if(id){
    this.clienteService.getCliente(id).subscribe((cliente) => this.cliente=cliente)
  }
})
}
update():void{

  this.clienteService.update(this.cliente)
  .subscribe(json =>{
    this.router.navigate(['/clientes'])
    Swal.fire('Cliente actualizado', `${json.mensaje}: ${json.cliente.nombre} `,'success')
  },
  err =>{
   this.errores=err.error.errors as string[];
   console.log(err.error.errors);
   console.log("Estoy desde el backend"+err.status);
  });
}
create():void{

 /*------primera
  console.log("clicked");
console.log(this.cliente);
 this.clienteService.create(this.cliente)
 .subscribe(response =>this.router.navigate(['/clientes'])
 );*/
 /*----------code tuturial--------------*/
 this.isSubmitting = true;
 this.clienteService.create(this.cliente)
 .subscribe(json =>{
  this.router.navigate(['/clientes'])
  Swal.fire('Nuevo cliente', `${json.mensaje}: ${json.cliente.nombre} `,'success')
  this.isSubmitting = false;
 },
 err =>{
  this.errores=err.error.errors as string[];
  console.log(err.error.errors);
  console.log("Estoy desde el backend"+err.status);
  this.isSubmitting = false;
 }
 );
}

}


<ul class="alert alert-danger" *ngIf="errores?.length">
    <li *ngFor="let err of errores">
        {{err}}

    </li>
</ul>
<div class="card bg-dark text-white my-5">
    <div class="card-header">{{titulo}}</div>
    <div class="card-body">

        <form (ngSubmit) = "create()" #clienteForm="ngForm">
            <div class="form-group row">
                <label for="nombre" class="col-form-label col-sm-2">Nombre</label>
        <div class="col-sm-6">
            <input type="text" class="form-control" [(ngModel)]="cliente.nombre" 
            name="nombre" #nombre="ngModel"><!--required  minlength="4"-->
            <div class="alert alert-danger" *ngIf="nombre.invalid && (nombre.dirty || nombre.touched)">
                <div *ngIf="nombre.errors?.['minlength']">
                    Nombre debe tener mínimo 4 caracteres
                </div>
                <div *ngIf="nombre.errors?.['required']">
                    Nombre es requerido
                </div>
            </div>
            </div>
            </div>
            <div class="form-group row">
            <label for="apellido" class="col-form-label col-sm-2">Apellido</label>
            <div class="col-sm-6">
                <input type="text" class="form-control" [(ngModel)]="cliente.apellido" name="apellido" 
               #apellido="ngModel"><!--required  minlength="4"-->
                <div class="alert alert-danger" *ngIf="apellido.invalid && (apellido.dirty || apellido.touched)">
                    <div *ngIf="apellido.errors?.['minlength']">
                        Apellido debe tener mínimo 4 caracteres
                    </div>
                    <div *ngIf="apellido.errors?.['required']">
                        Apellido es requerido
                    </div>
                </div>
            </div>
        </div>
            <div class="form-group row">
            <label for="email" class="col-form-label col-sm-2">Email</label>
            <div class="col-sm-6">
                <input type="email" class="form-control" [(ngModel)]="cliente.email" 
                name="email" #email="ngModel"><!--required email-->
                <div class="alert alert-danger" *ngIf="email.invalid && (email.dirty || email.touched)">
                    <div *ngIf="email.errors?.['email']">
                        Email debe tener formato válido
                    </div>
                    <div *ngIf="email.errors?.['required']">
                        Email es requerido
                    </div>
                </div>
            </div>
          </div>
            <div class="form-group row">
                <div class="col-sm-6">
                    <button class="btn btn-primary" role="button" (click)='create()'
             *ngIf="!cliente.id; else elseBlock" [disabled]="!clienteForm.form.valid" [disabled]="isSubmitting">Crear</button>
             <ng-template #elseBlock >
                 <button class="btn btn-primary" role="button" 
                 (click)="update()" [disabled]="!clienteForm.form.valid">Editar</button>
             </ng-template>
                </div>
            </div>
        </form>
    </div>

</div>
