import { Component } from '@angular/core';

@Component({
  selector: 'app-footer',
  templateUrl: './footer.component.html',
  styleUrls: ['./footer.component.css']
})
export class FooterComponent {
autor: any ={nombre: 'Oscar',apellido:'Sanabria'}
}
<footer class="bg-dark rounded-top text-center footer">

    <div class="container py-2">
        <p class="text-white my-2">&copy; {{autor.nombre + ''+autor.apellido}}</p>
    </div>
</footer>
